vec4 Effect2(vec2 uv) {
    // Create wave patterns with time offset
    float waveX = sin(uv.x * 4.0 + iTime) * 0.5 + 0.5;
    float waveY = sin(uv.y * 4.0 + iTime * 0.7) * 0.5 + 0.5;

    // Combine waves and add turbulence
    vec2 movement = vec2(waveX, waveY);
    movement += turbulent(movement * 10.0 + iTime).xx;

    // Create color cycling effect using HSL
    vec3 color = hsl(iTime * 0.5 + movement.x * PI, 0.7, 0.6);

    return vec4(color * turbulent(uv * 20.0 + iTime), 1.0);
}
