vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);
    vec2 coord = uv * 5.0;

    // Recursive sine wave pattern with fractal detail
    for(float i = 0.0; i < 6.0; i++) {
        float time = iTime * (i + 1.0) * 0.5;

        // Complex sine transformations
        coord.x += sin(coord.y * 3.0 + time) * 0.2;
        coord.y += cos(coord.x * 3.0 - time) * 0.2;

        // Fractal detail with noise and turbulence
        vec2 st = coord * pow(1.5, i);
        float noiseVal = noise(st * 0.1 + time) * pow(0.5, i);
        float turbulentVal = turbulent(st * 0.2 + time) * pow(0.3, i);

        // Color transformations
        vec3 color = hsl(
            sin(iTime * 0.2 + coord.x * 2.0) * 0.5 + 0.5,
            abs(sin(coord.y * 2.0 + time)) * 0.5 + 0.5,
            turbulentVal * 0.3 + 0.7
        );

        finalColor += hsl2rgb(color) * (noiseVal + 0.5) * pow(0.8, i);
    }

    return vec4(finalColor, 1.0);
}
