vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create flowing sine wave patterns
    float time = iTime * 0.5;
    float speed = 1.0;

    // X-direction flow
    float xFlow = sin(uv.x * 5.0 + time) * 0.5 + 0.5;
    // Y-direction flow
    float yFlow = sin(uv.y * 5.0 + time + PI) * 0.5 + 0.5;

    // Combine flows with turbulence
    vec2 flow = vec2(xFlow, yFlow);
    float turbulentFlow = turbulent(flow * 10.0 + time);

    // Color transitions using HSL
    vec3 hslColor = vec3(
        mod(time * 0.5, 1.0),  // Hue
        0.7,                   // Saturation
        0.6                    // Lightness
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add motion blur effect using noise
    float noiseValue = noise(uv * 10.0 + time) * 0.5;
    color += rgbColor * (turbulentFlow * 0.7 + noiseValue * 0.3);

    // Enhance the flowing effect with multiple sine layers
    for(float i = 0.0; i < 3.0; i++) {
        float freq = pow(2.0, i) * 5.0;
        color += sin(uv.x * freq + time) * 
                 sin(uv.y * freq + time + PI) *
                 rgbColor * 0.15;
    }

    return vec4(color, 1.0);
}
