vec4 Effect2(vec2 uv) {
    vec4 finalColor = vec4(0.0);

    // Create multiple overlapping circles with varying properties
    for(float i = 0.0; i < 100.0; i++) {
        // Randomize circle position and radius using noise
        vec2 pos = uv + vec2(noise(vec2(iTime * 0.5, i)), noise(vec2(i, iTime * 0.3)));
        float dist = length(uv - pos);

        // Create pulsating effect with time-based modulation
        float pulse = sin(iTime * 0.2 + i) * 0.5 + 0.5;
        float radius = 0.15 + noise(vec2(i, iTime)) * 0.05;

        // Calculate color and opacity based on distance and time
        if(dist < radius) {
            vec3 color = hsl(
                iTime * 0.2 + fract(i * 0.1),   // Hue variation over time
                0.8,                            // Saturation
                0.3 + (1.0 - dist / radius) * 0.4  // Lightness variation based on distance
            );

            float opacity = (1.0 - dist / radius) * pulse;
            finalColor.rgb = mix(finalColor.rgb, color, opacity);
        }
    }

    return finalColor;
}
