vec4 Effect2(vec2 uv) {
    // Animate time
    float time = iTime * 0.5;

    // Create sine wave patterns in both x and y directions
    vec2 st = uv * 8.0; // Scale coordinates for more waves
    float waveX = sin(st.x + time) * 0.5 + 0.5;
    float waveY = sin(st.y + time + PI/2.0) * 0.5 + 0.5;

    // Combine waves and add noise
    float wave = (waveX + waveY) * 0.5;
    wave += turbulent(vec3(uv * 4.0, time)) * 0.3;

    // Create rotating color palette using HSL
    vec3 colorHSL = vec3(
        mod(time * 0.2 + uv.x * 2.0 + uv.y * 3.0, 1.0), // Hue
        0.7 + wave * 0.3, // Saturation
        0.5 + sin(time + uv.x * PI) * 0.5 // Lightness
    );

    // Convert to RGB and apply fog based on distance from center
    vec3 color = hsl2rgb(colorHSL);
    float depth = length(uv);
    color *= exp(-depth * 0.1); // Fog effect

    return vec4(color, 1.0);
}
