vec4 Effect2(vec2 uv) {
    // Create a basic sine wave pattern
    float speed = 5.0;
    float frequency = 3.0;

    // Calculate position with time offset
    vec2 pos = uv * 2.0 - 1.0;
    float timeOffset = iTime * speed;

    // Create flowing lines using sine wave and noise
    float lineValue = sin(pos.x * frequency + timeOffset) * 0.5 + 0.5;
    lineValue += noise(vec3(uv * 4.0, iTime)) * 0.2; // Add noise for texture

    // Create color shifts using sine functions
    vec3 hslColor = vec3(
        sin(iTime * 0.5) * 0.5 + 0.5, // Hue variation over time
        1.0, // Saturation
        lineValue // Lightness based on line value
    );

    // Convert HSL to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    float turb = turbulent(vec3(uv * 4.0, iTime)) * 0.5 + 0.5;
    color *= turb; // Apply turbulence for dynamic intensity

    return vec4(color, 1.0);
}
