vec4 Effect2(vec2 uv) {
    // Rotate coordinates around center
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);

    // Create layered sine waves with time-based animation
    float wave1 = sin(iTime + length(st)*5.0) * 0.5;
    float wave2 = sin(iTime*0.8 + length(st)*3.0 + 1.57) * 0.4;

    // Add turbulence for organic feel
    vec2 noiseUV = st * 0.5 + iTime*0.2;
    float turb = turbulent(noiseUV);

    // Create color gradients using HSL
    vec3 color1 = hsl(0.6 + sin(iTime)*0.2, 0.8, 0.7);
    vec3 color2 = hsl(0.4 + cos(iTime*0.9), 0.8, 0.6);

    // Combine waves and colors
    float blend = (wave1 + wave2 + turb) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, blend);

    // Add lighting effect
    float light = sin(iTime*0.5 + radius*4.0) * 0.5 + 0.5;
    finalColor *= light;

    return vec4(finalColor, 1.0);
}
