vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to -1.0 to 1.0 range
    vec2 st = uv * 2.0 - 1.0;

    // Add time-based movement
    float time = iTime * 0.5;
    st.x += sin(time) * 0.5;
    st.y += cos(time) * 0.3;

    // Create sine wave patterns with varying frequencies
    float waveX = sin(st.x * 4.0 + time) * 0.5;
    float waveY = sin(st.y * 6.0 - time * 2.0) * 0.5;

    // Combine waves and add turbulence
    float factor = (waveX + waveY) * 0.5;
    factor += turbulent(vec3(st.x, st.y, time)) * 0.3;

    // Create dynamic color shifts using HSL
    vec3 hslColor = vec3(
        sin(time * 0.2) * 0.5 + 0.5,
        0.7,
        0.8 + factor * 0.2
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix colors based on wave pattern
    vec3 finalColor = mix(
        rgbColor,
        vec3(1.0, 0.5, 0.2),
        smoothstep(-1.0, 1.0, factor)
    );

    return vec4(finalColor * (1.0 + abs(factor) * 0.5), 1.0);
}
