vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.1; // Add rotation over time

    // Create a galaxy-like pattern using noise and turbulence
    float galaxy = turbulent(vec3(r * 5.0, theta * 4.0, iTime * 0.1));

    // Add star glow effect using high-frequency noise
    float stars = noise(vec3(st * 200.0, iTime)) * 0.5 + 0.5;
    stars = smoothstep(0.95, 1.0, stars);

    // Create color gradient for the galaxy
    vec3 galaxyColor = hsl2rgb(vec3(
        sin(r * 2.0 + iTime) * 0.4 + 0.6,
        0.7,
        0.5 + 0.5 * sin(theta * 3.0 + iTime)
    ));

    // Create star color
    vec3 starColor = vec3(1.0);

    // Combine galaxy and stars with glow effect
    vec3 finalColor = mix(
        galaxyColor,
        starColor,
        stars * exp(-r * 2.0) // Star glow falloff
    );

    return vec4(finalColor, 1.0);
}
