vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float theta = atan2(st.y, st.x) + iTime;  // Add time for rotation

    // Create hexagonal pattern using multiple noise layers
    float n1 = turbulent(vec3(st * 10.0, iTime));
    float n2 = turbulent(vec3(st * 5.0, iTime)) * 0.5;
    float n3 = turbulent(vec3(st * 2.0, iTime)) * 0.25;

    // Combine noise layers
    float pattern = (n1 + n2 + n3) / 1.75;

    // Create color gradient based on rotation angle
    vec3 hslColor = vec3(theta * 0.1, 0.8, sin(radius * 4.0 + iTime * 0.5));
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix pattern with color and output
    float finalValue = (pattern + sin(theta) * 0.5 + 0.5) * radius;
    return vec4(finalValue * rgbColor, 1.0);
}
