vec4 Effect2(vec2 uv) {
    // Base pulsation frequency
    float basePulse = sin(iTime * 0.5 + 1.0);

    // Dynamic color components based on time and position
    vec3 colorBase = hsl(
        0.6 + 0.4 * sin(iTime * 0.2 + uv.x * 8.0),
        0.7,
        0.5
    );

    // Add turbulence for dynamic movement
    float turb = turbulent(uv * 10.0 + vec2(iTime));

    // Create distance field from center
    vec2 center = vec2(0.5, 0.5);
    float distanceFromCenter = length(uv - center) * 2.0;

    // Layered sine functions for pulsation effect
    float layer1 = sin(distanceFromCenter * 3.0 + iTime) * 0.5;
    float layer2 = sin(distanceFromCenter * 6.0 + iTime * 0.7) * 0.4;
    float layer3 = sin(distanceFromCenter * 9.0 + iTime * 1.2) * 0.3;

    // Combine layers and add turbulence
    float intensity = (layer1 + layer2 + layer3) * 0.5 + turb * 0.3;

    // Final color calculation with dynamic hue shift
    vec3 finalColor = hsl2rgb(vec3(
        0.6 + 0.4 * sin(iTime * 0.2 + distanceFromCenter),
        0.7 + turb * 0.1,
        0.5 + intensity * 0.3
    ));

    return vec4(finalColor, 1.0);
}
