vec4 Effect2(vec2 uv) {
    // Create dynamic sine wave patterns with varying frequencies
    float x = sin(uv.x * 8.0 + iTime * 2.0) * 0.5 + 0.5;
    float y = sin(uv.y * 4.0 + iTime * 1.5) * 0.5 + 0.5;

    // Combine patterns and add turbulence
    vec2 st = uv * 8.0;
    float pattern = (x + y) * 0.5;
    pattern += turbulent(st + iTime) * 0.3;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(pattern * 0.6 + iTime, 1.0, 0.5));

    return vec4(color, 1.0);
}
