vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Rotate over time

    // Create square pattern using polar coordinates
    float sa = sin(a);
    float ca = cos(a);
    vec2 rotated = vec2(ca * st.x + sa * st.y, -sa * st.x + ca * st.y);

    // Calculate distance from square edges
    float d1 = abs(rotated.x) + abs(rotated.y);
    float d2 = max(abs(rotated.x), abs(rotated.y));
    float dist = min(d1, d2);

    // Create color shifts using HSL
    vec3 color = hsl(a * 0.5 + iTime, 1.0, 0.5);
    vec3 finalColor = hsl2rgb(color) * (1.0 - turbulent(vec3(uv * 4.0, iTime)));

    // Combine patterns and output
    return vec4(finalColor, 1.0);
}
