vec4 Effect2(vec2 uv) {
    // Create coordinate system centered at screen center
    vec2 st = uv - 0.5;
    st *= 1.5; // Scale up for better visibility

    // Calculate rotation angles based on time
    float rot1 = sin(iTime * 0.7);
    float rot2 = cos(iTime * 0.6 + PI/2);

    // Create rotating square pattern
    vec2 sq1 = abs(st) - 0.5;
    float mask1 = length(sq1) < 0.5 ? 1.0 : 0.0;

    vec2 sq2 = abs(rotate(st, rot2)) - 0.4;
    float mask2 = length(sq2) < 0.4 ? 1.0 : 0.0;

    // Combine masks
    float pattern = min(mask1, mask2);

    // Create pulsating color using HSV to RGB conversion
    vec3 color = hsv2rgb(vec3(sin(iTime * 0.5) * 0.5 + 0.5, 0.8, 0.5));

    // Add noise for extra dynamic effect
    float noiseFactor = noise(vec3(st.x*10, st.y*10, iTime)) * 0.3;
    color = mix(color, color * (1.0 + noiseFactor), 0.3);

    return vec4(pattern * color, 1.0);
}
