vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create a rotating geometric pattern using sine waves
    float pattern = sin(theta * 4.0 + iTime) * cos(r * 10.0 - iTime);

    // Generate pulsating color gradients
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.5, 1.0), // Pulsating hue
        0.7,
        0.5 + 0.5 * sin(iTime) // Pulsating lightness
    ));

    // Add noise and turbulence for organic movement
    float noiseValue = turbulent(vec3(uv * 2.0, iTime));
    color *= 1.0 + 0.2 * noiseValue;

    // Combine pattern with color
    vec4 finalColor = vec4(color * (0.5 + 0.5 * pattern), 1.0);

    return finalColor;
}
