vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern using sin/cos functions
    float angle = time + length(uv) * 2.0 * PI;
    vec2 rotatedUV = uv * vec2(cos(angle), sin(angle));

    // Generate plasma effect using turbulence and noise
    float t1 = turbulent(rotatedUV * 50.0 + time);
    float t2 = turbulent(rotatedUV * 25.0 + time * 0.5);

    // Create color shifts
    vec3 colorShifts = vec3(
        sin(time) * 0.5 + 0.5,
        sin(time * 1.3) * 0.5 + 0.5,
        sin(time * 2.0) * 0.5 + 0.5
    );

    // Combine effects and convert to RGB
    vec3 finalColor = hsv2rgb(vec3(
        t1 * 360.0 + time * 180.0,
        1.0,
        1.0
    )) * colorShifts;

    return vec4(finalColor, 1.0);
}
