vec4 Effect2(vec2 uv) {
    // Center offset and rotation
    vec2 center = 0.5 + sin(iTime * 0.5) * 0.3;
    float angle = atan2(uv.y - center.y, uv.x - center.x);
    float radius = length(uv - center);

    // Swirling effect with time-based rotation
    angle += iTime * 0.5;
    radius *= 0.5 + turbulent(vec2(radius, iTime)) * 0.3;

    // Color transitions based on angle and radius
    vec3 color = hsl(angle * 0.1, radius * 0.5 + 0.5, 0.7);
    color = hsv2rgb(vec3(
        fract(hsv.x + iTime * 0.1),
        hsv.y,
        hsv.z * (1.0 - turbulent(radius))
    ));

    // Add noise texture and trails
    vec2 noiseUV = uv * 5.0 + iTime;
    float noiseValue = noise(noiseUV);
    color += noiseValue * 0.3;

    // Fading trail effect
    vec2 trailUV = uv - angle * 0.1;
    float trailStrength = turbulent(trailUV) * 0.5;
    color = mix(color, color * 0.7, trailStrength);

    return vec4(color, 1.0);
}
