uniform float time;
uniform vec2 resolution;
void main() {
    vec2 uv = gl_FragCoord.xy / resolution.xy;

    const float numCells = 8.0;
    vec2 grid = floor(uv * numCells) / numCells;
    vec2 center = grid + vec2(0.5)/numCells;
    vec2 pos = uv - center;

    float r = length(pos);
    float theta = atan(pos.y, pos.x) + time * 0.5; // Adding rotation over time

    // Compute turbulence
    float turb = turbulent(vec3(r, time, 0.0)) * 0.5 + 0.5;

    vec3 colorHSL = vec3(theta / (2.0 * pi), 1.0 - r * 2.0, 0.5);
    colorHSL.z += turb; // Adding turbulence to lightness

    vec3 colorRGB = hsl2rgb(colorHSL);

    gl_FragColor = vec4(colorRGB, 1.0);
}
