vec4 Effect2(vec2 uv) {
    // Base terrain height using perlin noise
    float time = iTime * 0.5;
    vec3 pos = vec3(uv * 10.0, time);

    // Create flowing movement
    float flow = sin(time + uv.x * 2.0) * 0.5;
    pos.y += flow;

    // Generate terrain with multiple noise layers
    float height = noise(pos * 0.3) * 0.5;
    height += noise(pos * 0.6) * 0.3;
    height += noise(pos * 1.2) * 0.2;

    // Create color transitions based on height and time
    vec3 color = hsl(
        (height + sin(time + uv.x)) * 0.5 + 0.5,
        0.7,
        abs(sin(time + height * 2.0) * 0.5 + 0.5)
    );

    // Convert HSL to RGB
    color = hsl2rgb(color);

    // Add fog effect based on distance from camera
    float fogFactor = smoothstep(0.0, 1.0, length(uv));
    vec3 fogColor = vec3(0.2, 0.3, 0.5) * (sin(time * 0.5) + 0.5);

    // Combine terrain color with fog
    color = mix(color, fogColor, fogFactor);

    // Add ambient lighting based on height
    float light = sin(height * pi + time) * 0.5 + 0.5;
    color *= vec3(light);

    return vec4(color, 1.0);
}
