vec4 Effect2(vec2 uv) {
    // Create a grid pattern with hexagonal cells
    vec2 st = uv * 10.0;

    // Add time-based movement to the grid
    float speed = 0.5;
    vec2 offset = vec2(sin(iTime * speed), cos(iTime * speed)) * 0.3;
    st += offset;

    // Generate noise values for each cell
    float n1 = noise(st);
    float n2 = noise(st + vec2(1.0, 0.5));

    // Combine noise values to create a hexagonal pattern
    float grid = mix(n1, n2, fract(st.x * 0.5 + st.y * 0.7071));

    // Add turbulence for dynamic movement
    vec3 pos = vec3(uv * 5.0, iTime);
    float turb = turbulent(pos) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        sin(iTime + uv.x * 2.0) * 0.5 + 0.5,
        sin(iTime + uv.y * 2.0) * 0.5 + 0.5,
        0.7
    );

    // Convert HSL to RGB and apply grid pattern
    vec3 color = hsl2rgb(hslColor);
    color *= mix(1.0, 0.5, abs(grid - turb));

    return vec4(color, 1.0);
}
