vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a grid of dots using noise and turbulence
    float n = 10.0; // Number of dots per axis
    for(float i = -n; i <= n; i++) {
        for(float j = -n; j <= n; j++) {
            vec2 pos = vec2(i, j) / n;
            pos += noise(vec3(pos * 10.0 + iTime, iTime)) * 0.1;

            // Add turbulence to create movement
            float turb = turbulent(vec3(pos * 5.0 + iTime));
            pos += turb * 0.2;

            // Calculate distance from current UV to dot position
            vec2 dist = uv - pos;
            float d = length(dist);

            // Create glowing effect with fading trails
            float glow = max(1.0 - d * 3.0, 0.0);
            glow *= max(1.0 - abs(iTime - mod(turb * 5.0 + iTime, 2.0)) * 2.0, 0.0);

            // Add color with HSL to RGB conversion
            vec3 hslColor = vec3((i + j) / (2*n) * 360.0, 1.0, 0.5);
            vec3 rgbColor = hsl2rgb(hslColor);

            // Mix colors and add to final color
            color += rgbColor * glow;
        }
    }

    return vec4(color, 1.0);
}
