vec4 Effect2(vec2 uv) {
    // Create a warping pattern using noise and turbulence
    vec2 st = uv * 5.0;
    float t = iTime * 0.5;

    // Add some movement to the coordinates
    vec2 offset = vec2(sin(t + st.x), cos(t + st.y)) * 0.1;
    vec2 new_uv = uv + offset;

    // Create a hexagonal pattern using polar coordinates
    vec2 center = vec2(0.5, 0.5);
    vec2 toCenter = new_uv - center;
    float radius = length(toCenter) * 3.0;
    float angle = atan2(toCenter.y, toCenter.x);

    // Add some turbulence and noise
    float turb = turbulent(vec3(new_uv * 10.0, t));
    float noiseVal = noise(vec3(new_uv * 5.0, t)) * 0.5 + 0.5;

    // Create a color based on angle and radius
    vec3 color = hsl2rgb(vec3(angle / (2.0 * PI), radius / 10.0, noiseVal));

    // Add some geometric patterns
    float circle = smoothstep(0.9, 1.0, radius);
    float hexagon = sin(radius) * 0.5 + 0.5;

    // Combine everything with some blending
    color *= mix(vec3(circle), vec3(hexagon), noiseVal);
    color += vec3(turb * 0.2);

    return vec4(color, 1.0);
}
