vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = uv * 2.0 - 1.0;

    // Add time-based rotation
    float angle = iTime * 0.5;
    vec2 rotated = vec2(
        cos(angle)*st.x - sin(angle)*st.y,
        sin(angle)*st.x + cos(angle)*st.y
    );

    // Create square wave patterns with varying frequencies
    vec3 pattern1 = abs(sin(rotated.xyxy * 4.0 + iTime));
    vec3 pattern2 = abs(step(0.5, fract(sin(rotated.xyxy * 8.0) + iTime)));

    // Combine patterns using turbulence
    float turb = turbulent(vec3(st.x*0.5, st.y*0.5, iTime*0.1));
    vec3 combined = pattern1 * pattern2;

    // Invert colors based on noise threshold
    float invertThreshold = 0.5 + 0.5*sin(iTime);
    vec3 color = combined > invertThreshold ? (1.0 - combined) : combined;

    // Add color transformation and time-based shifts
    color = hsl2rgb(vec3(
        fract(hsv2rgb(combined).x * iTime),
        0.7 + 0.3*sin(iTime*0.5),
        0.5 + 0.5*turb
    ));

    return vec4(color, 1.0);
}
