vec4 Effect2(vec2 uv) {
    // Create base color with time-based hue
    vec3 color = hsl(uv.x * 5.0 + iTime * 2.0, 1.0, 0.5);

    // Add noise and turbulence for flowing effect
    float n = noise(vec3(uv * 4.0, iTime * 0.5));
    color += vec3(turbulent(vec2(uv * 8.0 + iTime), 4) * 0.1);

    // Create RGB variation with time offset
    vec3 rgbColor = hsl2rgb(vec3(
        uv.x * 3.0 + iTime * 0.5,
        0.7,
        0.6
    ));

    // Combine colors and add noise modulation
    color = mix(color, rgbColor, n * 0.5 + 0.5);

    return vec4(hsl2rgb(color), 1.0);
}
