vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec2 st = uv - 0.5;

    // Polar coordinates for rotation
    float r = length(st);
    float theta = atan2(st.y, st.x) + time;

    // Geometric pattern with noise
    float pattern = sin(theta * 8.0 + time) * cos(r * 4.0 - time) * 
                   abs(sin(theta * 3.0)) / (cos(theta * 5.0 + time) + 0.1);

    // Add noise for texture
    vec2 noisePos = st * 5.0 + time;
    float noiseVal = noise(noisePos);
    pattern += noiseVal * 0.3;

    // Color transformation with pulsating effect
    vec3 baseColor = hsl(
        sin(time) * 180.0,
        0.7 + 0.3 * sin(r * 5.0 + time),
        0.5 + 0.5 * cos(theta * 4.0)
    );

    // Mix colors based on pattern
    vec3 finalColor = mix(
        baseColor,
        hsl(
            theta * 180.0 / PI,
            0.6,
            0.7 + 0.3 * sin(r * 2.0 - time)
        ),
        abs(pattern) * 0.5 + 0.5
    );

    return vec4(finalColor, 1.0);
}
