vec4 Effect2(vec2 uv) {
    // Moving gradient background
    vec3 grad1 = hsl2rgb(vec3(0.5 * sin(iTime * 0.5) + 0.5, 0.7, 0.8));
    vec3 grad2 = hsl2rgb(vec3(0.5 * sin(iTime * 0.5 + PI) + 0.5, 0.6, 0.7));

    // Cellular noise pattern
    float scale = 0.1;
    vec2 grid1 = uv * scale;
    vec2 grid2 = uv * scale + vec2(sin(iTime)*0.1, cos(iTime*0.3)*0.1);

    float n1 = noise(grid1) * 0.5 + 0.5;
    float n2 = noise(grid2) * 0.5 + 0.5;

    // Combine noise patterns
    float pattern = (n1 + n2) * 0.5;

    // Animate the pattern
    vec2 animUV = uv + vec2(sin(iTime*0.3)*0.2, cos(iTime*0.4)*0.2);
    float animatedPattern = pattern + noise(animUV * scale * 10.0) * 0.3;

    // Mix gradient and noise
    vec3 finalColor = mix(grad1, grad2, animatedPattern);

    return vec4(finalColor, 1.0);
}
