vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float a = atan(pos.y, pos.x);
    // Create flower petal shape using sine waves and noise
    float petal = sin(a * 6.0 + iTime) * 0.5 + 0.5;
    petal *= cos(a * 3.0 - iTime) * 0.5 + 0.5;
    petal *= r < 1.0 ? 1.0 : 0.0;
    // Add noise for organic feel
    vec2 noisePos = pos * 4.0 + iTime;
    float n = noise(noisePos) * 0.3;
    petal += n;
    // Create color transition from center to edges
    vec3 color = hsl2rgb(vec3(0.16, 0.8, 0.5)); // Yellow center
    vec3 edgeColor = hsl2rgb(vec3(0.7, 0.7, 0.5)); // Pink edges
    float blend = smoothstep(0.5, 1.0, r);
    color = mix(color, edgeColor, blend);
    // Add lighting effect
    vec3 lightDir = normalize(vec3(sin(iTime), cos(iTime), 1.0));
    vec3 normal = vec3(-pos.x, -pos.y, 1.0);
    normal = normalize(normal);
    float ambient = 0.5;
    float diffuse = max(dot(lightDir, normal), 0.0) * 0.8;
    color *= (ambient + diffuse);
    // Combine everything
    vec3 finalColor = mix(vec3(0.0), color, petal);

    return vec4(finalColor, 1.0);
}
