vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating pattern using time
    theta += iTime * 0.5;
    // Add noise and turbulence to create dynamic patterns
    vec3 noisePos = vec3(uv * 10.0 + iTime, 0.0);
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Create kaleidoscopic effect by repeating pattern
    theta = mod(theta, pi * 2.0);
    float angle = theta / (pi * 2.0);

    // Generate color transitions using HSL
    vec3 color1 = hsl(angle * 360.0, 0.8, 0.5);
    vec3 color2 = hsl((angle + 0.3) * 360.0, 0.8, 0.5);

    // Combine colors and noise
    vec3 finalColor = mix(color1, color2, turb);
    finalColor *= r < 1.0 ? smoothstep(0.0, 1.0, r) : 0.0;

    return vec4(finalColor * 2.0, 1.0);
}
