vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add pulsing effect based on time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    r *= 1.0 + pulse;
    // Create rotating color wheels using HSL colors
    vec3 color1 = hsl(theta * 0.2 + iTime * 0.5, 1.0, 0.5);
    vec3 color2 = hsl(theta * 0.4 + iTime * 0.7, 1.0, 0.6);
    // Add noise and turbulence for dynamic patterns
    float scale = 8.0;
    float timeOffset = iTime * 0.5;
    vec3 noisePattern = turbulent(vec3(uv * scale + timeOffset, iTime));

    // Combine colors with noise pattern
    vec3 finalColor = mix(color1, color2, noisePattern.x);
    finalColor *= sin(r * 8.0 + iTime) * 0.5 + 0.5;
    finalColor += turbulent(vec2(uv * 16.0)) * 0.2;
    return vec4(finalColor, 1.0);
}
