vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 st = uv - vec2(0.5);
    st.x += iTime * 0.1;

    // Create flowing sine wave patterns
    float flow = sin(2.0 * st.x + iTime) * 0.5;
    flow += sin(4.0 * st.x + iTime * 0.5) * 0.3;
    flow += turbulent(st * 10.0 + vec2(iTime)) * 0.2;

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3(
        (st.x + iTime) * 0.4,   // Hue
        0.8,                    // Saturation
        0.5 + flow * 0.2        // Lightness
    ));

    vec3 color2 = hsl2rgb(vec3(
        (st.x + iTime) * 0.4 + 0.5,   // Hue offset
        0.8,                            // Saturation
        0.5 + sin(1.5 * st.y + iTime) * 0.3  // Lightness variation
    ));

    // Mix colors based on flow pattern
    vec3 finalColor = mix(color1, color2, smoothstep(-0.5, 0.5, flow));

    return vec4(finalColor, 1.0);
}
