vec4 Effect2(vec2 uv) {
    // Create base wave pattern using sine function
    float wave = sin(uv.x * 5.0 + iTime) * 0.5;
    wave += sin(uv.x * 10.0 + iTime * 2.0) * 0.3;

    // Add pulsation effect
    float pulse = cos(iTime * 2.0);
    wave *= (pulse * 0.5 + 0.5);

    // Add turbulence based on UV coordinates and time
    vec2 noiseUV = uv * 10.0 + iTime;
    float turb = turbulent(noiseUV) * 0.3;
    wave += turb;

    // Create color transitions using HSL
    float hue = uv.x * 5.0 + iTime * 2.0;
    vec3 hslColor = vec3(hue, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Output the final color with alpha
    return vec4(rgbColor, 1.0);
}
