vec4 Effect2(vec2 uv) {
    // Create base noise pattern with time offset
    vec3 noise1 = noise(vec3(uv * 0.5 + sin(iTime * 0.5), iTime));
    vec3 noise2 = noise(vec3(uv * 0.7 - sin(iTime * 0.6), iTime * 0.8));

    // Combine noise layers with different scales
    float baseNoise = (turbulent(uv * 4.0 + iTime) + 
                      turbulent(uv * 2.0 + iTime * 0.5)) * 0.5;

    // Create color cycling effect using HSL to RGB conversion
    vec3 hslColor = vec3(
        sin(iTime * 0.1 + baseNoise * 2.0) * 0.5 + 0.5,  // Hue
        abs(sin(iTime * 0.3 + noise1.x)) * 0.7 + 0.3,     // Saturation
        abs(cos(iTime * 0.4 + noise2.y)) * 0.6 + 0.4      // Lightness
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    // Add turbulence effect for more dynamic movement
    float turb = turbulent(vec2(uv.x + iTime, uv.y - iTime) * 8.0);
    rgbColor *= (turb * 0.5 + 0.5);  // Scale turbulence to affect brightness

    return vec4(rgbColor, 1.0);
}
