vec4 Effect2(vec2 uv) {
    // Create mirrored patterns using symmetry
    vec2 st = fract(uv * 8.0);
    st = abs(st - 0.5) * 2.0;

    // Add rotating sine wave movement
    float time = iTime * 0.1;
    float sinX = sin(time + uv.x * pi);
    float sinY = cos(time + uv.y * pi);

    // Combine mirrored patterns with sine waves
    vec2 motion = vec2(sinX, sinY) * 0.5 + 0.5;
    vec2 finalUV = mix(uv, motion, 0.5);

    // Add turbulence for dynamic effect
    float turb = turbulent(finalUV * 4.0) * 0.5 + 0.5;

    // Create smooth color transitions using HSL
    vec3 hslColor = vec3(turb * 360.0, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
