vec4 Effect2(vec2 uv) {
    // Add some animation over time
    float time = iTime * 0.5;

    // Create a network pattern using noise and turbulence
    vec3 point3D = vec3(uv * 10.0, time);
    float noiseVal = turbulent(point3D);

    // Create moving lines effect
    vec2 st = uv * 10.0 + time;
    float line = abs(turbulent(vec3(st, time)));

    // Color transitions using HSL
    vec3 color = hsl2rgb(vec3(time * 0.1, 0.7, 0.5));

    // Combine effects and create glowing lines
    float glow = min(1.0 - abs(line), 0.05);
    vec3 finalColor = mix(vec3(0.0), color, glow);

    return vec4(finalColor, 1.0);
}
