vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a grid of hexagons
    vec2 st = uv * 5.0;
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Calculate rotation for each hexagon
    float angle = sin(ipos.x + ipos.y) * time * 0.2;
    mat2 rot = mat2(cos(angle), -sin(angle),
                    sin(angle), cos(angle));
    vec2 pos = rot * (fpos - 0.5);

    // Create pulsating effect
    float pulse = (sin(time * 0.5) + 1.0) * 0.5;
    float radius = 0.4 + 0.2 * pulse;

    // Calculate distance from center
    float dist = length(pos);

    vec3 color = vec3(0.0);

    if (dist < radius) {
        // Create color based on position and time
        float hue = fract(time * 0.1 + pos.x * 2.0 + pos.y * 3.0);
        vec3 hslColor = vec3(hue, 0.8, 0.5 + dist * 0.5);
        color = hsl2rgb(hslColor);
    }

    return vec4(color, 1.0);
}
