vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv - 0.5;
    float radius = length(st);
    float angle = atan2(st.y, st.x);

    // Add time-based rotation
    angle += iTime * 0.5f;

    // Create color wheels using HSL colors
    vec3 color1 = hsl(angle * 0.5f + 0.5f, 1.0f, 0.5f);
    vec3 color2 = hsl(angle * 0.5f + 0.5f + 0.333f, 1.0f, 0.5f);
    vec3 color3 = hsl(angle * 0.5f + 0.5f - 0.333f, 1.0f, 0.5f);

    // Add turbulence for plasma effect
    float turb = turbulent(uv * 4.0f + iTime) * 0.5f + 0.5f;
    vec3 plasmaColor = mix(color1, color2, turb);
    plasmaColor = mix(plasmaColor, color3, turb * 0.5f);

    // Add radius variation
    float rVariation = sin(iTime * 0.5f + radius * 4.0f) * 0.2f;
    radius += rVariation;

    // Combine everything
    vec3 finalColor = hsl2rgb(vec3(
        angle * 0.1f + 0.5f,
        1.0f,
        radius * 0.5f + 0.5f
    ));

    finalColor = mix(finalColor, plasmaColor, 0.7f);

    return vec4(finalColor, 1.0);
}
