vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create spider web pattern using noise and turbulence
    float radial = sin(50.0 * a + iTime) * noise(vec3(a, r, iTime));
    float spiral = sin(r * 10.0 - a) * turbulent(vec2(r, a));
    // Combine patterns
    float filament = (radial + spiral) * 0.5;

    // Add color variation
    vec3 color = hsl(a / (2.0 * PI), r * 0.5 + 0.5, 0.7);
    vec3 rgbColor = hsl2rgb(color);
    // Add noise to value channel for depth
    float valueNoise = noise(vec3(r, a, iTime));
    rgbColor *= 1.0 + valueNoise * 0.3;
    return vec4(rgbColor, filament * 0.8 + 0.2);
}
