vec4 Effect2(vec2 uv) {
    // Create base sphere shape
    vec2 st = uv * 2.0 - 1.0;
    float dist = length(st);
    float glow = 1.0 - smoothstep(0.8, 1.0, dist);

    // Add noise and turbulence for dynamic effects
    vec3 noisePos = vec3(uv * 5.0 + iTime, iTime * 0.2);
    float turb = turbulent(noisePos) * 0.5;

    // Create color shifts using HSL
    float hue = (iTime * 0.1 + dist * 0.5) * 360.0;
    vec3 hslColor = vec3(hue, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add surface ripples
    float ripple = sin(dist * 10.0 + iTime * 2.0) * 0.1;
    dist += ripple;

    // Combine effects with lighting
    vec3 lightDir = normalize(vec3(sin(iTime), cos(iTime), 0.5));
    vec3 normal = normalize(vec3(st.x, st.y, sqrt(1.0 - dot(st,st))));
    float light = max(0.0, dot(normal, lightDir)) * 0.6 + 0.4;

    // Final color
    vec3 finalColor = rgbColor * (glow + turb) * light;

    return vec4(finalColor, 1.0);
}
