vec4 Effect2(vec2 uv) {
    // Create time-based movement
    float t = iTime * 0.2;

    // Layered sine waves with different frequencies and amplitudes
    vec2 st = uv + vec2(sin(t + uv.x*10.), cos(t + uv.y*10.));
    float s = sin(5.*st.x + t) * 0.3;
    s += sin(10.*st.y + t*2.) * 0.2;
    s += sin(15.*st.x + t*3.) * 0.1;

    // Add noise for randomness
    s += noise(st + t) * 0.1;

    // Create pulsating effect with turbulence
    vec2 pos = turbulent(uv * 10. + t);

    // Convert to HSL color space with time-based hue cycling
    vec3 hslColor = vec3(
        mod(s*0.5 + t*2., 1.),   // Hue
        0.7,                     // Saturation
        0.5 + s*0.2              // Lightness
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
