vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create base color with shifting hues
    float hue = 0.2 + 0.3 * sin(iTime * 0.5);
    vec3 baseColor = hsl(hue, 1.0, 0.5);

    // Add flowing noise pattern
    float flow = turbulent(vec3(uv * 4.0, iTime));
    color += baseColor * (flow + 0.5);

    // Add secondary noise layer for detail
    float detail = noise(vec2(uv * 2.0) + vec2(iTime)) * 0.5;
    color += mix(baseColor, vec3(1.0), detail);

    return vec4(color, 1.0);
}
