vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create rotating sine wave pattern with multiple frequencies
    float pattern = sin(r * 8.0 + a * 4.0) * 0.5 +
                   sin(r * 16.0 + a * 2.0) * 0.3 +
                   sin(r * 32.0 + a) * 0.2;

    // Add RGB phase shifts
    vec3 color = vec3(
        pattern * sin(iTime + 0.0),
        pattern * sin(iTime + 2.094), // 120 degrees offset
        pattern * sin(iTime + 4.188)  // 240 degrees offset
    );

    // Add noise for texture
    vec2 noiseUV = uv * 5.0 + iTime;
    float n = noise(noiseUV);
    color += n * 0.3;

    // Convert to HSL and back to RGB for vibrant colors
    vec3 hslColor = vec3(
        a * 0.15 + iTime * 0.1,
        0.7,
        0.6 + abs(pattern) * 0.4
    );
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
