vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    float angle = atan2(uv.y, uv.x);
    float radius = length(uv);

    // Animate pattern over time
    angle += iTime * 0.5;
    radius += sin(iTime) * 0.5;

    // Create orbiting sine wave patterns for each color channel
    vec3 color = vec3(
        sin(angle + iTime) * 0.5 + 0.5,
        sin(angle - iTime * 0.7 + PI) * 0.5 + 0.5,
        sin(angle + iTime * 1.2 + PI*2) * 0.5 + 0.5
    );

    // Add radial gradient effect
    float gradient = pow(radius, 2.0);
    color *= vec3(gradient);

    // Mix in noise for texture
    vec2 noiseUV = uv * 10.0 + iTime;
    float n = noise(noiseUV) * 0.5 + 0.5;
    color = mix(color, vec3(n), 0.3);

    return vec4(color, 1.0);
}
