vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 st = uv * 10.0;

    // Create two sets of points with offset
    vec2 p1 = sin(cos(st + iTime)) * 0.5 + 0.5;
    vec2 p2 = cos(sin(st + iTime + 1.0)) * 0.5 + 0.5;

    // Calculate vector between points
    vec2 dir = normalize(p2 - p1);

    // Get angle of direction for color transition
    float angle = atan2(dir.y, dir.x) / (2.0 * PI);

    // Add noise to create dynamic movement
    vec2 noiseOffset = noise(vec3(st + iTime)) * 0.5;
    p1 += noiseOffset;
    p2 -= noiseOffset;

    // Calculate distance from current point to both sets of points
    float d1 = length(uv - p1);
    float d2 = length(uv - p2);

    // Mix colors based on proximity and angle
    vec3 color1 = hsl(angle + iTime * 0.5, 0.7, 0.6);
    vec3 color2 = hsl(angle - iTime * 0.5, 0.7, 0.6);

    // Combine colors with noise modulation
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, turbulent(vec3(uv + iTime)))));

    return vec4(finalColor, 1.0);
}
