vec4 Effect2(vec2 uv) {
    // Create base color using HSL values
    vec3 baseColor = hsl(uv.x * 0.5 + 0.5, 1.0, 0.5);

    // Calculate wave offset based on time and position
    float waveOffset = sin(iTime * 0.5 + uv.y * pi) * 0.2;
    vec2 shiftedUV = uv + vec2(waveOffset, 0.0);

    // Add turbulence effect to the color shift
    float noiseValue = turbulent(shiftedUV * 10.0) * 0.5 + 0.5;

    // Shift hue using sine function for animation
    float hueShift = sin(iTime * 0.2 + uv.x * pi) * 0.3 + 0.7;
    vec3 shiftedColor = hsl(hueShift, noiseValue, 0.5);

    // Convert to RGB and output
    return vec4(hsl2rgb(shiftedColor), 1.0);
}
