vec4 Effect2(vec2 uv) {
    // Create a wobbling pattern using noise and turbulence
    vec2 st = uv * 5.0;

    // Add some time-based movement
    float time = iTime * 0.5;

    // Create a base checkerboard pattern with noise warping
    vec2 warp = vec2(
        sin(time + st.x) * 0.3,
        cos(time + st.y) * 0.3
    );

    // Add turbulence for more complex movement
    vec2 p = st + warp;
    float n1 = noise(p*2.0) * 0.5;
    float n2 = noise(p*4.0) * 0.5;
    float turb = turbulent(p);

    // Create color from HSL values
    vec3 hslColor = vec3(
        (n1 + 0.5) * 360.0,   // Hue varies with noise
        0.7,                  // Saturation fixed
        0.5 + turb*0.5        // Lightness varies with turbulence
    );

    // Convert HSL to RGB and mix with another color layer
    vec3 color = hsl2rgb(hslColor);

    // Add some depth using multiple noise layers
    float depth = (noise(st) + noise(st * 2.0)) * 0.5;
    color *= 1.0 - abs(depth * 0.5);

    return vec4(color, 1.0);
}
