vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create base noise pattern with time animation
    float noise1 = noise(vec3(st * 5.0 + iTime, iTime));
    float noise2 = turbulent(vec3(st * 10.0 + iTime*0.5, iTime));
    // Mix noise values and radius for color input
    vec3 colorInput = vec3(
        r * 0.4 + noise1 * 0.6,
        r * 0.7 - noise2 * 0.3,
        mod(iTime * 0.5, 2.0)
    );
    // Convert to HSL and then RGB
    vec3 color = hsl2rgb(hsl(
        mod(colorInput.x + a * 0.5 + iTime*0.1, 1.0),
        0.7 + noise(vec3(st * 20.0)) * 0.3,
        0.5 + sin(r * 4.0 + iTime) * 0.5
    ));
    // Add pulsing effect based on radius and time
    float pulse = sin(iTime * 2.0 + r * 8.0);
    color *= 1.0 + pulse * 0.3;
    return vec4(color, 1.0);
}
