vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create hexagonal grid pattern
    vec2 st = uv * 5.0;
    vec2 ipos = floor(st + iTime*0.5);
    vec2 pos = fract(st + iTime*0.5) - 0.5;

    // Convert to polar coordinates for hexagons
    float theta = atan(pos.y, pos.x);
    float r = length(pos);

    // Create hexagonal shape using trigonometric functions
    float hex = cos(theta * 3.0 + iTime*2.0) * 0.5 + 0.5;
    hex *= sin(theta * 3.0 + iTime*2.0) * 0.5 + 0.5;

    // Add turbulence for dynamic distortion
    float turb = turbulent(vec2(r, theta + iTime));
    hex += turb * 0.3;

    // Create color transitions
    vec3 hslColor = vec3(
        fract(iTime*0.1 + ipos.x * 0.5) * 360.0,
        0.7,
        0.5 + sin(r * 20.0 + iTime) * 0.2
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine patterns with wave distortion
    float wave = sin(r * 8.0 + theta * 4.0 + iTime*2.0) * 0.1;
    hex += wave;

    // Add to final color
    color += hex * rgbColor * (1.0 - abs(pos.y));

    return vec4(color, 1.0);
}
