vec4 Effect2(vec2 uv) {
    // Create a gradient background using polar coordinates
    vec2 center = vec2(0.5);
    vec2 toCenter = uv - center;
    float dist = length(toCenter);
    float angle = atan2(toCenter.y, toCenter.x);

    // Create rotating hexagonal pattern
    float time = iTime * 0.5;
    mat2 rot = mat2(cos(time), sin(time), -sin(time), cos(time));
    vec2 st = uv * 4.0; // Scale up for multiple hexagons
    st = rot * (st - 1.0) + 1.0; // Rotate around center

    // Hexagonal tiling pattern
    vec2 q = fract(st);
    float d = max(abs(q.x - 0.5), abs(q.y - 0.5));

    // Pulsating color effect
    float hue = time * 0.1 + angle * 0.3;
    vec3 color1 = hsl(hue, 0.7, 0.6);
    vec3 color2 = hsl(hue + 0.5, 0.7, 0.6);

    // Add noise and turbulence
    float noiseVal = noise(vec3(st * 0.5, time)) * 0.1;
    float turbulentVal = turbulent(vec2(st.x * 0.5, st.y * 0.5)) * 0.1;

    // Combine effects
    vec3 finalColor = mix(color1, color2, smoothstep(0.4, 0.6, d + noiseVal + turbulentVal));
    finalColor *= 1.0 - dist * 0.8; // Gradient falloff

    return vec4(finalColor, 1.0);
}
