vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a noise pattern with multiple layers
    vec3 layer1 = turbulent(vec3(uv * 8.0 + time, 0.0));
    vec3 layer2 = turbulent(vec3(uv * 4.0 + time * 0.7, 1.0));

    // Combine the layers and scale for better contrast
    float noiseValue = (layer1.x * 0.5 + layer2.x * 0.5) * 0.5 + 0.5;

    // Create a flowing color effect using HSV
    vec3 hsvColor = vec3(
        fract(time * 0.1 + noiseValue * 0.2) * PI,
        0.7 + noiseValue * 0.3,
        0.8 + abs(sin(time)) * 0.2
    );

    // Convert HSV to RGB and output
    vec3 rgbColor = hsv2rgb(hsvColor);
    return vec4(rgbColor, 1.0);
}
