vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float t = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Create base noise pattern with time variation
    float baseNoise = noise(rotatedUv * 10.0 + vec2(sin(t), cos(t)));

    // Add turbulence for more complex patterns
    float turb = turbulent(rotatedUv * 5.0) * 0.5;

    // Create color variations using HSL
    vec3 color1 = hsl(baseNoise * 360.0, 0.7, 0.5);
    vec3 color2 = hsl((baseNoise + turb) * 360.0, 0.8, 0.6);

    // Mix colors based on noise values
    vec3 finalColor = mix(color1, color2, abs(turb));

    // Add some distortion to the pattern
    vec2 warp = sin(rotatedUv * 5.0) * 0.1;
    vec2 finalUv = rotatedUv + warp;

    return vec4(finalColor, 1.0);
}
